<?php/** * @usage Session Destroy Hook * @method wp_logout_session_destroy */add_action('wp_logout', 'wp_logout_session_destroy');if (!function_exists('wp_logout_session_destroy')) :    function wp_logout_session_destroy()    {        session_destroy();    }endif;/** * @usage Adds active class for active page in menu * @method special_nav_class */add_filter('nav_menu_css_class', 'special_nav_class', 10, 2);function special_nav_class($classes, $item){    if (in_array('current-menu-item', $classes)) {        $classes[] = 'active ';    }    return $classes;}/** * @usage Remove Admin Bar * @method disableAdminBar *///add_action('init','disableAdminBar');if (!function_exists('disableAdminBar')) :    function disableAdminBar()    {        remove_action('admin_footer', 'wp_admin_bar_render', 1000);        remove_action('wp_footer', 'wp_admin_bar_render', 1000);    }endif;/** * @usage Remove Admin Bar Styling Admin Panel * @method remove_admin_bar_style_backend */if (!function_exists('remove_admin_bar_style_backend')) :    function remove_admin_bar_style_backend()    {        echo '<style>body.admin-bar #wpcontent, body.admin-bar #adminmenu { padding-top: 0px !important; }		#dup-add-link{display:none !important;}		</style>';    }endif;add_filter('admin_head', 'remove_admin_bar_style_backend');/** * @usage Remove Admin Bar Styling Front * @method remove_admin_bar_style_frontend */if (!function_exists('remove_admin_bar_style_frontend')) :    function remove_admin_bar_style_frontend()    {        echo '<style type="text/css" media="screen">			html { margin-top: 0px !important; }			* html body { margin-top: 0px !important; }		</style>';    }endif;add_filter('wp_head', 'remove_admin_bar_style_frontend', 99);/** * @usage Menu Elements Remove from WP-ADMIN * @method remove_menus() * @global $menu *//* add_action('admin_menu', 'remove_p2c_admin_menus'); */if (!function_exists('remove_p2c_admin_menus')):    function remove_p2c_admin_menus()    {        global $menu;        $restricted = array(__('Plugins'), __('Tools'), __('Users'), __('Comments'));        end($menu);        while (prev($menu)) {            $value = explode(' ', $menu[key($menu)][0]);            if (in_array($value[0] != NULL ? $value[0] : "", $restricted)) {                unset($menu[key($menu)]);            }        }    }endif;/** * @usage Update Wordpress Footer * @filter update_footer_admin */add_filter('admin_footer_text', 'update_footer_admin');if (!function_exists('update_footer_admin')):    function update_footer_admin()    {        echo '  ' . date('Y') . ' &copy; Copyrights |  <a href="' . site_url() . '" target="_blank">' . get_bloginfo('name') . '</a>';    }endif;/** * @usage Remove Wordpress Admin Panel Version * @method nb_footer_version */if (!function_exists('nb_footer_version')) :    function nb_footer_version()    {        return '';    }endif;add_filter('update_footer', 'nb_footer_version', 9999);/** * @usage Removing Default Wordpress Admin Widgets * @method nb_remove_dashboard_widgets */if (!function_exists('nb_remove_dashboard_widgets')) :    function nb_remove_dashboard_widgets()    {        remove_meta_box('dashboard_right_now', 'dashboard', 'normal');        remove_meta_box('dashboard_recent_comments', 'dashboard', 'normal');        remove_meta_box('dashboard_incoming_links', 'dashboard', 'normal');        remove_meta_box('dashboard_plugins', 'dashboard', 'normal');        remove_meta_box('dashboard_quick_press', 'dashboard', 'normal');        remove_meta_box('dashboard_primary', 'dashboard', 'normal');        remove_meta_box('dashboard_secondary', 'dashboard', 'normal');        remove_meta_box('dashboard_recent_drafts', 'dashboard', 'normal');        remove_submenu_page('themes.php', 'theme-editor.php');    }endif;/* add_action('admin_init', 'nb_remove_dashboard_widgets',1); *//** * @usage Removes Default Metaboxes * @method nb_remove_default_metaboxes */if (!function_exists('nb_remove_default_metaboxes')) :    function nb_remove_default_metaboxes()    {        remove_meta_box('postcustom', 'page', 'normal'); // Custom Fields metabox        remove_meta_box('postcustom', 'post', 'normal'); // Custom Fields metabox        remove_meta_box('commentstatusdiv', 'page', 'normal'); // Discussion metabox        remove_meta_box('commentstatusdiv', 'post', 'normal'); // Discussion metabox        remove_meta_box('commentsdiv', 'page', 'normal'); // Comments metabox        remove_meta_box('commentsdiv', 'post', 'normal'); // Comments metabox        remove_meta_box('slugdiv', 'page', 'normal'); // Slug metabox        remove_meta_box('slugdiv', 'post', 'normal'); // Slug metabox        remove_meta_box('authordiv', 'page', 'normal'); // Author metabox        remove_meta_box('authordiv', 'post', 'normal'); // Author metabox        remove_meta_box('revisionsdiv', 'page', 'normal'); // Revisions metabox        remove_meta_box('revisionsdiv', 'post', 'normal'); // Revisions metabox    }endif;/* add_action('admin_menu','nb_remove_default_metaboxes'); *//** * @usage Removing Default Widgets. * @method nb_remove_default_widgets */if (!function_exists('nb_remove_default_widgets')) :    function nb_remove_default_widgets()    {        unregister_widget('WP_Widget_Pages');        unregister_widget('WP_Widget_Search');    }endif;/* add_action('widgets_init','nb_remove_default_widgets', 11); *//** * @usage Removing Update Notices * @method wp_hide_update */remove_action('wp_head', 'wp_generator');if (!current_user_can('edit_users')) {    add_action('init', create_function('$a', "remove_action( 'init', 'wp_version_check' );"), 2);    add_filter('pre_option_update_core', create_function('$a', "return null;"));}if (!function_exists('wp_hide_update')) :    function wp_hide_update()    {        remove_action('admin_notices', 'update_nag', 3);    }endif;/* add_action('admin_menu','wp_hide_update'); *//** * @usage Removing Welcome Panel Widget * @method wp_welcome_panel */remove_action('welcome_panel', 'wp_welcome_panel');/** * @usage Hide Screen Options DropMenu * @method wp_ welcome_panel *///add_filter('screen_options_show_screen', '__return_false');/** * @usage Update Admin Panel Logo * @method nb_admin_bar_render */if (!function_exists('nb_admin_bar_render')) :    function nb_admin_bar_render()    {        global $wp_admin_bar;        $wp_admin_bar->remove_menu('about');        $wp_admin_bar->remove_menu('wporg');        $wp_admin_bar->remove_menu('documentation');        $wp_admin_bar->remove_menu('support-forums');        $wp_admin_bar->remove_menu('feedback');        $wp_admin_bar->remove_menu('new-content');        $wp_admin_bar->remove_menu('comments');        $defaults = array(            'title' => '<span class="ab-icon" title="' . get_bloginfo('title') . '"></span>',            'href' => get_bloginfo('url'),            'parent' => false,            'id' => 'wp-logo',            'meta' => ''        );        $wp_admin_bar->add_menu($defaults);    }endif;add_action('wp_before_admin_bar_render', 'nb_admin_bar_render');/** * @usage Update Admin Panel Logo * @method update_admin_login_logo */if (!function_exists('update_admin_login_logo')) :    function update_admin_login_logo()    {        echo '<style type="text/css">			h1 a { background:url(' . get_stylesheet_directory_uri() . '/images/admin/logo.png) no-repeat !important;height: 40px !important;width: 152px !important;}		</style>';    }endif;add_action('login_head', 'update_admin_login_logo');/** * @usage Update Admin Login Header URL * @method update_admin_login_url */if (!function_exists('update_admin_login_url')) :    function update_admin_login_url()    {        return get_bloginfo('url');    }endif;add_filter('login_headerurl', 'update_admin_login_url');/** * @usage Update Admin Login Header Title * @method update_admin_login_title */if (!function_exists('update_admin_login_title')) :    function update_admin_login_title()    {        return get_bloginfo('title');    }endif;add_filter('login_headertitle', 'update_admin_login_title');function my_login_logo(){ ?>    <style type="text/css">        #login h1 a, .login h1 a {            background-image: url(<?php echo  get_header_image()  ?>);            padding-bottom: 30px;        }    </style><?php }add_action('login_enqueue_scripts', 'my_login_logo');?>